import React from 'react';
import './Page1.css';
import Nav from 'react-bootstrap/Nav';
import { useNavigate } from 'react-router-dom';
import { Col, Form, Row } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import Button from 'react-bootstrap/Button';
import { toast } from 'react-toastify';
import Login from './login';

const Page1 = () => {
  const navigate = useNavigate();
  const { handleLogin } = Login(); 

  return (
    <>
      <div className="main">
        <div className="bg"></div>
        <div className="bg2"></div>
        <div className="bg3">
          <p style={{ fontWeight: 600, marginLeft: 45, fontSize: 22, paddingTop: 10 }}>Login</p>
        </div>
        <div className="bg4">
          <div className="login-container">
            <p style={{ fontWeight: 600, fontSize: 25 }}>
              Welcome! To find your lost item, please log in with your credentials.
            </p>
            <Form className="form">
              <Form.Group as={Row} className="mb-3">
                <Form.Label column sm={12} style={{ fontSize: 20, fontWeight: 400, marginTop: 10 }}>
                  Email
                </Form.Label>
                <Col sm={12}>
                  <Form.Control id='email' style={{ maxWidth: '300px', width: '100%', marginTop: 5 }} placeholder="Enter Email ID" />
                </Col>
              </Form.Group>

              <Form.Group as={Row} className="mb-3">
                <Form.Label column sm={12} style={{ fontSize: 20, fontWeight: 400, marginTop: 10 }}>
                  Password
                </Form.Label>
                <Col sm={12}>
                  <Form.Control id='pass' style={{ maxWidth: '200px', width: '100%', marginTop: 10 }} type="password" placeholder="Enter your password" />
                </Col>
              </Form.Group>

              <Button variant="secondary" id='p1_b1' style={{ marginTop: 40 }} size="20" active onClick={handleLogin}>
                Login
              </Button>

              <p style={{ color: 'grey', fontSize: 15, marginTop: 25, display: 'flex', alignItems: 'center' }}>
                Don't have an account?
                <Nav.Link href="/page2" id='l1' style={{ marginLeft: 5 }}>Sign up</Nav.Link>
              </p>
            </Form>
          </div>
        </div>
      </div>
    </>
  );
};

export default Page1;
