import React, { useState, useEffect, useRef } from "react";
import { fetchLostItems } from "./lost.js";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import "bootstrap/dist/css/bootstrap.min.css";
import "../Components/Lost.css";
import emptyImage from "../Assets/empty.png";
import Loading from "./loding";
import Spinner from "react-bootstrap/Spinner";
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';
// import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useNavigate } from 'react-router-dom';

const Lost = ({ setLoading }) => {
  const [lostItems, setLostItems] = useState([]);
  const [loading, setLocalLoading] = useState(true);

  useEffect(() => {
    const loadItems = async () => {
      setLoading?.(true); 
      setLocalLoading(true); 

      const items = await fetchLostItems();
      setLostItems(items);

      setLoading?.(false);
      setLocalLoading(false);
    };

    loadItems();
  }, [setLoading]);
  const [showClaimModal, setShowClaimModal] = useState(false);

const handleClaimClick = () => {
    setShowClaimModal(true);
};

const handleClaimClose = () => {
    setShowClaimModal(false);
};

const navigate = useNavigate();

const handleLostClick = () => {
  navigate('/lost');
};

const handleFoundClick = () => {
  navigate('/found');
};
const handleListingClick = () => {
  navigate('/listing');
};
const handleMyChatClick = () => {
  navigate('/chat');
};

const handleMyResponseClick = () => {
  navigate('/response');
};

const handleMyHomeClick = () =>{
  navigate("/");
}




  return (
    <div >
      <Loading loading={loading} />

<div className="redlabel"></div>

<div className="navbar_lost">
  <div className="home_nav" onClick={handleMyHomeClick}>Home</div>
  <div className="lost_nav" onClick={handleLostClick}>Lost</div>
  <div className="found_nav" onClick={handleFoundClick}>Found</div>
  <div className="listing_nav" onClick={handleListingClick}>My Listing</div>
  <div className="response_nav" onClick={handleMyResponseClick}>My Responses</div>
  <div className="chat_nav" onClick={handleMyChatClick}>My Chats</div>
 
</div>

{loading ? null : lostItems.length > 0 ? (

  <div className="main_container_lost">
    <div
      className="lost-container"  >
      {lostItems.map((item) => (
        <Card key={item.id} style={{ width: "18rem" }}>
          <Card.Img variant="top" src={item.imageUrl || "https://via.placeholder.com/150"} alt="Lost Item"  style={{ height: "300px" }}
          />
          
          <Card.Body>
            <Card.Title>{item.itemName}</Card.Title>
            <Card.Text>{item.description}</Card.Text>
            <Button
      variant="secondary"
      className="custom-button mt-3"
      id="claim_button"
      onClick={handleClaimClick}
    >
      Claim Item
    </Button>
            {/* <Button variant="primary">Delete</Button> */}
          </Card.Body>
        </Card>
      ))}
    </div>
  </div>
) : (
  <div style={{ textAlign: "center", marginTop: "20px" }}>
    <img src={emptyImage} alt="No items found" style={{ width: "300px", height: "auto" }} />
    <h3>No items found</h3>
  </div>
)}
<Modal show={showClaimModal} onHide={handleClaimClose} centered>
  <Modal.Header closeButton>
    <Modal.Title>Answer the question to claim item</Modal.Title>
  </Modal.Header>
  <Modal.Body>
    <Form>    
      <Form.Group className="mb-3">
        <Form.Label>User Question</Form.Label>
        <Form.Control as="textarea" rows={3} placeholder="Please answer correctly to claim your product...." required />
      </Form.Group>
      <Button variant="primary" className="mt-3">Claim</Button>
    </Form>
  </Modal.Body>
</Modal>

      </div>      
  );
};

export default Lost;
